package chap11;

// Math 클래스 : 수학 계산에 사용할 수 있는 메소드를 제공
public class MathEx01_p504 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub	
		double v = Math.PI;
		System.out.println("Math.Pi =" + v);
		System.out.println("올림 ceil = " + Math.ceil(v));
		System.out.println("버림(내림) floor = " + Math.floor(v));
		System.out.println("반올림 round = " + Math.round(v));
		System.out.println("올림 ceil = " + Math.ceil(v));
		System.out.println("올림 ceil = " + Math.ceil(v));
	}

}
